      printf("ROM+MBC5+RUMBLE+SRAM");
      break;

    case 30:
      printf("ROM+MBC5+RUMBLE+SRAM+BATTERY");
      break;

    case 31:
      printf("POCKET CAMERA");
      break;

    case 253:
      printf("Bandai TAMA5");
      break;

    case 254:
      printf("Hudson HuC-3");
